import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from "react-router-dom";
import { makeStyles } from 'tss-react/mui';
import { Container, Typography, TextField, Button } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: theme.spacing(10)
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing(2)
  }
}));

const LoginWithoutForm = () => {
  const classes = useStyles();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const users = useSelector(state => state.users);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const loginnn = () => {
    const payload = users.find(user => user.email === email && user.password === password)
    if (payload) {
      dispatch({
        type: 'LOGIN',
        payload
      })
      alert("login successfully")
      navigate('/Home');
    } else {
      alert("wrong credential !!")
    }
  };

  return (
    <Container className={classes.container}>
      <Typography variant="h5">Login</Typography>
      <TextField
        label="Email"
        type="email"
        name="email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        margin="normal"
        required
      />
      <TextField
        label="Password"
        type="password"
        name="password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        margin="normal"
        required
      />
      <Button variant="contained" color="primary" onClick={loginnn}>
        Submit
      </Button>
      <Typography variant="body1">
        Don't have an account? <Link to="/Registration" >Register</Link> now.
      </Typography>
    </Container>
  );
};

export default LoginWithoutForm;
